// Initialize the map
var map = L.map('map').setView([20.5937, 78.9629], 5);

L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
    attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
}).addTo(map);

var userIcon = L.icon({
    iconUrl: 'https://img.icons8.com/color/48/000000/marker.png',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32]
});

var userMarker;

// Predefined array of recycling centers
var recyclingCenters = [
    { lat: 20.4625, lng: 85.8828, name: "CDA Recycling Center", image: "images/img.jpg" },
    { lat: 20.4620, lng: 85.8780, name: "Barabati Recycling Center", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQQ8YCYa-WHl21neMzUYnUWqt7YDPemLH5UVA&s" },
    { lat: 20.4543, lng: 85.8822, name: "Cuttack Railway Station Recycling Center", image: "data:image/jpeg;base64," },
    { lat: 20.4627, lng: 85.8344, name: "Orissa High Court Recycling Center", image: "images/img2.jpg" },
    { lat: 20.4630, lng: 85.8835, name: "Cuttack Medical College Recycling Center", image: "data:image/jpeg;base64," },
    { lat: 20.4540, lng: 85.8020, name: "Mahanadi Barrage Recycling Center", image: "images/img2.jpg" },
    { lat: 20.4600, lng: 85.8720, name: "Netaji Birth Place Museum Recycling Center", image: "images/img.jpg" },
    { lat: 20.3300, lng: 85.8180, name: "Nandankanan Zoological Park Recycling Center", image: "data:image/jpeg;base64," },
    { lat: 20.4635, lng: 85.8820, name: "Cuttack Stadium Recycling Center", image: "data:image/jpeg;base64," },
    { lat: 20.4620, lng: 85.8360, name: "Odisha State Museum Recycling Center", image: "data:image/jpeg;base64," }
];

function getDistance(lat1, lon1, lat2, lon2) {
    var R = 6371; // Radius of the earth in km
    var dLat = deg2rad(lat2 - lat1);
    var dLon = deg2rad(lon2 - lon1); 
    var a = 
        Math.sin(dLat/2) * Math.sin(dLat/2) +
        Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) * 
        Math.sin(dLon/2) * Math.sin(dLon/2)
    ; 
    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
    var d = R * c; // Distance in km
    return d;
}

function deg2rad(deg) {
    return deg * (Math.PI/180);
}

function updateMap(userLat, userLon, radius) {
    // Clear existing markers
    map.eachLayer(function (layer) {
        if (layer instanceof L.Marker && layer !== userMarker) {
            map.removeLayer(layer);
        }
    });

    var resultsList = document.getElementById('results-list');
    resultsList.innerHTML = '';

    // Calculate distances and filter centers
    var nearestCenters = recyclingCenters
        .map(center => ({
            ...center,
            distance: getDistance(userLat, userLon, center.lat, center.lng)
        }))
        .filter(center => center.distance <= radius)
        .sort((a, b) => a.distance - b.distance);

    nearestCenters.forEach(function (center) {
        // Add marker to map
        L.marker([center.lat, center.lng]).addTo(map)
            .bindPopup(`<b>${center.name}</b><br>Distance: ${center.distance.toFixed(2)} km`);

        // Add to sidebar
        var resultItem = document.createElement('div');
        resultItem.className = 'result-item';
        resultItem.innerHTML = `
            <img src="${center.image}" alt="${center.name}">
            <div class="result-info">
                <h3>${center.name}</h3>
                <p>${center.distance.toFixed(2)} km</p>
            </div>
        `;
        resultsList.appendChild(resultItem);
    });

    // Adjust map view to fit all markers
    var group = new L.featureGroup(map._layers);
    map.fitBounds(group.getBounds().pad(0.1));
}

if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(function (position) {
        var userLat = position.coords.latitude;
        var userLon = position.coords.longitude;

        map.setView([userLat, userLon], 13);

        userMarker = L.marker([userLat, userLon], { icon: userIcon }).addTo(map)
            .bindPopup('You are here!')
            .openPopup();

        document.getElementById('search-button').addEventListener('click', function () {
            var radius = parseFloat(document.getElementById('search-input').value);
            if (!isNaN(radius)) {
                updateMap(userLat, userLon, radius);
            } else {
                alert("Please enter a valid radius in kilometers.");
            }
        });

        // Initially show centers within 50km
        updateMap(userLat, userLon, 50);

    }, function () {
        alert("Unable to retrieve your location.");
    });
} else {
    alert("Geolocation is not supported by this browser.");
}